    toggleLike: async (_, args, {request, checkIfAuthenticated}) => {
      checkIfAuthenticated(request);
      const {postId} = args;
      const {user} = request;
      const filterOptions = {
        AND: [
          {
            user: {
              id: user.id,
            },
          },
          {
            post: {
              id: postId,
            },
          },
        ],
